def TashkhisDef():
    global DataBase
    global Leaf
    global LeafList
    global LeafList2
    global LeafColor
    global LeafColorList
    global LeafColorList2
    global FlowerTrueOrFalse
    global FlowerTrueOrFalseList
    global FlowerTrueOrFalseList2
    global FlowerColor 
    global FlowerColorList
    global FlowerColorList2 
    global Stem
    global StemList
    global StemList2
    global Smell
    global SmellList
    global SmellList2  
    global Common_Elements
    global Common_Elements2
    global Result_List
    global Result_List2

    

    for i in database:
        if Leaf.upper() == i["leaf"].upper():
            LeafList.append(i["name_fa"])
            LeafList2.append(i["name_en"])

    for i in database:
        if LeafColor.upper() == i["leaf_color"].upper():
            LeafColorList.append(i["name_fa"])
            LeafColorList2.append(i["name_en"])
        elif LeafColor.upper() == "MIX":
            if "and" in i["leaf_color"]:
                LeafColorList.append(i["name_fa"])
                LeafColorList2.append(i["name_en"])
        elif LeafColor.upper() == "AND" : 
            if i["leaf_color"].upper() != "GREEN" and i["leaf_color"].upper() != "LIGHT GREEN" and i["leaf_color"].upper() != "DARK GREEN":
                if not("AND" in i["leaf_color"].upper()):
                    if i["leaf_color"].upper() != "NONE" : 
                        LeafColorList2.append(i["name_en"])

    for i in database:
        if FlowerTrueOrFalse == i["flower"]:
            FlowerTrueOrFalseList.append(i["name_fa"])
            FlowerTrueOrFalseList2.append(i["name_en"])

    for i in database:
        if FlowerColor.upper() == i["flower_color"].upper():
            FlowerColorList.append(i["name_fa"])
            FlowerColorList2.append(i["name_en"])

    for i in database : 
        if Stem.upper() == i["stem"].upper():
            StemList.append(i["name_fa"])
            StemList2.append(i["name_en"])

    for i in database : 
        if Smell == i["smell"]:
            SmellList.append(i["name_fa"])
            SmellList2.append(i["name_en"])
        
    Common_Elements = set(LeafList) & set(LeafColorList) & set(FlowerTrueOrFalseList) & set(FlowerColorList) & set(StemList) & set(SmellList)
    Common_Elements2 = set(LeafList2) & set(LeafColorList2) & set(FlowerTrueOrFalseList2) & set(FlowerColorList2) & set(StemList2) & set(SmellList2)






    Result_List= list(Common_Elements)
    Result_List2 = list(Common_Elements2)
    Result_List.sort()
    Result_List2.sort()




   
    Tashkhis.grid_forget()

    if len(Result_List) > 0:
        if len(Result_List) - 1 == CalC :
            global Calc
            Calc = 0
            Natige.config(text=f"Your desired flowers: {Result_List2[CalC]}")
        else : 
            Natige.config(text=f"Your desired flowers: {Result_List2[CalC]}")
    else :
        Natige.config(text="Unfortunately, the flower you requested was not found.")


    global ImagesOfResult
    ImagesOfResult = []
    


    k= 0 

    for i in Result_List2 :
        if i in globals():
            ImagesOfResult.append(globals()[i])
            k=k+1
    global ImageNatije
    ImageNatije = tk.Label(window,image=ImagesOfResult[CalC])
    Natige.grid(row=-0,column=0)
    ImageNatije.place(x=400 ,y=30)
    back.place(x=150,y=700)
    confirm_button.place(x=650,y=700)
    Calculator.place(x=1150,y=700)
    # nof.place(x=0,y=0)
    # window.grid_rowconfigure(0,weight=1)
    window.grid_columnconfigure(0,weight=1)


    


