class Searcher(tk.Entry):
    def __init__(self, master, database, **kwargs):
        super().__init__(master, **kwargs)
        self.database = database
        self.popup = None
        self.hover_info = None
        self.bind("<KeyRelease>", self.on_type)

    def on_type(self, event=None):
        typed = self.get()
        if typed == "":
            self.hide_popup()
            return

        matches = [item for item in self.database if item['name_fa'].startswith(typed)]

        if not matches:
            self.hide_popup()
            return

        if self.popup:
            self.popup.destroy()

        self.popup = tk.Toplevel(self)
        self.popup.wm_overrideredirect(True)
        self.popup.wm_geometry(f"+{self.winfo_rootx()}+{self.winfo_rooty() + self.winfo_height()}")

        for item in matches:
            full_info = f"{item['name_fa']}"
            label = tk.Label(self.popup, text=full_info, bg="white", anchor="w", padx=5)
            label.pack(fill="both", expand=True)

            label.bind("<Enter>", lambda e, l=label, i=item: self.show_hover_info(e, l, i))
            label.bind("<Leave>", lambda e: self.hide_hover_info())
            label.bind("<Button-1>", lambda e, name=item['name_fa']: self.select_item(name))

    def select_item(self, name):
        self.delete(0, tk.END)
        self.insert(0, name)
        self.hide_popup()

    def hide_popup(self):
        if self.popup:
            self.popup.destroy()
            self.popup = None
        self.hide_hover_info()

    def show_hover_info(self, event, label, item):
        if self.hover_info:
            self.hover_info.destroy()

        self.hover_info = tk.Toplevel(self)
        self.hover_info.wm_overrideredirect(True)
        x = label.winfo_rootx() + label.winfo_width() + 5
        y = label.winfo_rooty()
        self.hover_info.wm_geometry(f"+{x}+{y}")

        frame = tk.Frame(self.hover_info, bg="#ccffcc", padx=10, pady=5)
        frame.pack()
        frame.attributes = {}
        frame.attributes['alpha'] = 0

        name = item.get("name_fa", "نامشخص")
        leaf_type = item.get("leaf", "نامشخص")
        for i in database:
            if name == i :
                x= i["leaf_name"]
                color = item.get("color", str(x))
        smell = "دارد" if item.get("smell", False) else "ندارد"

        tk.Label(frame, text=name, font=("B Nazanin", 14, "bold"), bg="#ccffcc").pack(anchor="center", pady=(0, 10))
        tk.Label(frame, text=f"نوع برگ: {leaf_type} 🌿", bg="#ccffcc", anchor="w").pack(anchor="w")
        tk.Label(frame, text=f"رنگ برگ: {color}", bg="#ccffcc", anchor="w").pack(anchor="w")
        tk.Label(frame, text=f"بو: {smell} 👃", bg="#ccffcc", anchor="w").pack(anchor="w")

        color_frame = tk.Frame(frame, width=50, height=50, bg=color)
        color_frame.pack(anchor="w", pady=5)

        def animate_color():
            import random
            try:
                r = lambda: random.randint(100, 255)
                color_frame.config(bg=f'#{r():02x}{r():02x}{r():02x}')
                color_frame.after(300, animate_color)
            except:
                pass

        animate_color()
        self.fade_in(self.hover_info)

    def hide_hover_info(self):
        if self.hover_info:
            self.hover_info.destroy()
            self.hover_info = None

    def fade_in(self, window, alpha=0):
        alpha += 0.1
        if alpha >= 1:
            window.attributes("-alpha", 1)
            return
        window.attributes("-alpha", alpha)
        window.after(30, lambda: self.fade_in(window, alpha))


